import styled from "styled-components";

export const PageAuth = styled.div`
  display: flex;
  align-items: stretch;
  height: 100vh;
`

export const Aside = styled.aside`
  flex: 7;
  background-color: #18265A;
  color: #fff;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 120px 80px;

  img {
    max-width: 530px;
  }

  strong {
    font: 700 64px "Roboto", sans-serif;
    line-height: 90px;
    color: #F3AE02;
  }

  span {
    font-size: 48px;
    line-height: 90px;
    margin-top: 16px;
    color: #f8f8f8;
  }
`
export const Main = styled.main `
    flex: 8;
    display: flex;
    align-items: center;
    justify-content: center;

`
