import styled from "styled-components";

export const SimulatorSection = styled.section`
  background: #fff;
  height: 22.5em;
  width: 24em;
  border-radius: 8px;
  margin: 4.5em 3em;

    section{
        display: flex;
        justify-content: space-around;
        align-items: center;
        padding: 1em 0;
        &:first-child{
            padding: 3em 0 1em;
        }

        &:nth-child(3){
            padding: 0.5em 0;
        }
        &:nth-child(4){
            padding: 0.5em 0;
        }
        span{
            margin-right: 4px;
            font-weight: 500;
        }
    }
`;
